CREATE TABLE `facultate`.`student` (`idstudent` INT UNSIGNED NOT NULL AUTO_INCREMENT , `nume` VARCHAR(50) NOT NULL , `prenume` VARCHAR(50) NOT NULL , `email` VARCHAR(100) NOT NULL , `grupa` ENUM(1,2,3,4) NOT NULL , `status` ENUM('admis','respins','neevaluat') NOT NULL DEFAULT 'neevaluat' , `dataadaugare` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`idstudent`), UNIQUE (`email`)) ENGINE = InnoDB;